  methods: {

    interpretCards() {
        this.loading = true;
        let endpoint = '';

        // 根据 currentTab 的值选择相应的接口
      if (this.currentTab === 'threeCards') {
        endpoint = '/zhipu/tarot-three-cards';
      } else if (this.currentTab === 'sevenCards') {
        endpoint = '/zhipu/tarot-seven-cards';
      } else if (this.currentTab === 'riyun' || this.currentTab === 'easy-riyun') {
        endpoint = '/zhipu/tarot-riyun';
      }else if(this.currentTab === 'easy-riyun'){
        endpoint = '/zhipu/tarot-easy-riyun';
      }else if (this.currentTab === 'zhouyun') {
        endpoint = '/zhipu/tarot-zhouyun';
      } else if (this.currentTab === 'yueyun') {
        endpoint = '/zhipu/tarot-yueyun';
      } else if (this.currentTab === 'nianyun') {
        endpoint = '/zhipu/tarot-nianyun';
      } else {
        // 对于动态生成的额外 tab，直接构造接口
        endpoint = `/zhipu/tarot/${this.currentTab}`;
      }

        // 设置请求参数和头部
        const config = {
        headers: {
            'Access-Control-Allow-Origin': '*',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
        };

        // 判断是否需要传递 message 参数
        if (this.currentTab === 'threeCards' || this.currentTab === 'sevenCards') {
        config.params = { message: this.message };
        }

        // 发起请求
        this.$axios.get(endpoint, config)
        .then(response => {
            this.tarotResponse = response.data.interpretation;
            this.cards = response.data.tarotCards;
            this.loading = false;
        })
        .catch(error => {
            this.loading = false;
            if (error.response && error.response.status === 403) {
            // 处理 403 错误
            alert('请登录！');
            this.$router.push({ path: '/' }); // 跳转到登录页面
            } else {
            console.error('Error interpreting cards:', error);
            }
        });
    },

    updateFilteredCards(selectNumber) {
      let group = '';
      if (selectNumber === 1) group = this.selectedGroup1;
      if (selectNumber === 2) group = this.selectedGroup2;
      if (selectNumber === 3) group = this.selectedGroup3;

      let filtered = [];
      if (group === "majorArcana") {
        filtered = Object.values(this.tarotCards).slice(0, 44);
      } else if (group === "cups") {
        filtered = Object.values(this.tarotCards).slice(45, 72);
      } else if (group === "wands") {
        filtered = Object.values(this.tarotCards).slice(73, 99);
      } else if (group === "pentacles") {
        filtered = Object.values(this.tarotCards).slice(100, 128);
      } else if (group === "swords") {
        filtered = Object.values(this.tarotCards).slice(128, 156);
      }

      if (selectNumber === 1) this.filteredCards1 = filtered;
      if (selectNumber === 2) this.filteredCards2 = filtered;
      if (selectNumber === 3) this.filteredCards3 = filtered;
    },
    jiepai() {
      this.loading = true; // 开始请求时设置 loading 为 true

      // 验证是否选择了三张牌和输入了问题
      if (!this.selectedCard1 || !this.selectedCard2 || !this.selectedCard3 || !this.jiepaiQuestion) {
        alert("请填写完整的解牌信息！");
        this.loading = false;
        return;
      }

      // 准备要发送的数据
      const payload = {
        message: this.jiepaiQuestion,
        cards: [this.selectedCard1, this.selectedCard2, this.selectedCard3],
      };

      // 向后端发送解牌请求
      this.$axios.post('/zhipu/tarot-jiepai', payload, {
        headers: {
          'Access-Control-Allow-Origin': '*', // 允许跨域
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      })
        .then(response => {
          this.tarotResponse = response.data.interpretation;
          this.cards = response.data.tarotCards;
          this.loading = false;
        })
        .catch(error => {
          this.loading = false;
          if (error.response && error.response.status === 403) {
            alert('请登录！');
            this.$router.push({ path: '/' }); // 跳转到登录页面
          } else {
            console.error('Error interpreting cards:', error);
          }
        });
    },


    logout() {
      // 清除本地存储的 JWT token
      localStorage.removeItem('token');
      alert('已成功退出登录');
      this.$router.push({ path: '/' }); // 跳转到登录页面
    },
    goToLogin() {
      // 跳转到登录页面
      this.$router.push({ path: '/' });
    },
    setTab(tab) {
      this.currentTab = tab; // 切换 Tab
    },
    searchCard() {
      const query = this.searchQuery.trim().toLowerCase();
      if (query === "") {
        this.filteredCards = [];
      } else {
        this.filteredCards = Object.values(this.tarotCards).filter(card =>
          card.title.toLowerCase().includes(query)
        );
      }
    }

  }
}
</script>

